mapper.select:
SELECT
T1.ORDER_STATUS AS orderStatus,COUNT(T1.ORDER_STATUS) AS orderCount,T4.PAY_TYPE AS payType
FROM
T_ORDER T1
LEFT JOIN T_SYSTEM_PAY_TYPE T4 ON T1.PAY_TYPE_ID = T4.PAY_TYPE_ID
WHERE
T1.CUST_ID = #{custId}

	AND T1.FLOW_ID LIKE CONCAT('%',#{flowId},'%' )


	AND T1.SUPPLY_NAME LIKE CONCAT('%',#{supplyName},'%' )


	 AND T4.PAY_TYPE=#{payType} 


	 AND T1.CREATE_TIME >= #{createBeginTime} 


	 AND T1.CREATE_TIME <= #{createEndTime} 

GROUP BY ORDERSTATUS,T4.PAY_TYPE

		SELECT s.pay_type_name,s.pay_type,
		
		from t_order o,t_system_pay_type s
		WHERE o.pay_type_id=s.pay_type_id
		AND  o.flow_Id=#{flowId}
		
			AND o.cust_Id=#{custId}
		
		
			AND o.supply_id=#{supplyId}

SELECT
T1.ORDER_STATUS AS orderStatus,COUNT(T1.ORDER_STATUS) AS orderCount,T4.PAY_TYPE AS payType
FROM
T_ORDER T1
LEFT JOIN T_SYSTEM_PAY_TYPE T4 ON T1.PAY_TYPE_ID = T4.PAY_TYPE_ID
LEFT JOIN T_USERMANAGE_ENTERPRISE T2 ON T1.CUST_ID = T2.ENTERPRISE_ID
WHERE
T1.SUPPLY_ID = #{supplyId}

	AND T1.FLOW_ID LIKE CONCAT('%',#{flowId},'%' )


	AND T1.CUST_NAME LIKE CONCAT('%',#{custName},'%' )


	 AND T4.PAY_TYPE=#{payType} 


	 AND T1.CREATE_TIME >= #{createBeginTime} 


	 AND T1.CREATE_TIME <= #{createEndTime} 


	AND T2.PROVINCE = #{province}


	AND T2.CITY = #{city}


	AND T2.DISTRICT = #{district}

GROUP BY ORDERSTATUS,T4.PAY_TYPE

SELECT ,T4.PAY_TYPE,T4.PAY_TYPE_NAME FROM T_ORDER T1 LEFT JOIN T_SYSTEM_PAY_TYPE T4 ON T1.PAY_TYPE_ID = T4.PAY_TYPE_ID LEFT JOIN T_USERMANAGE_ENTERPRISE T2 ON T1.CUST_ID = T2.ENTERPRISE_ID WHERE T1.SUPPLY_ID = #{supplyId}
AND T1.FLOW_ID LIKE CONCAT('%',#{flowId},'%' )
AND T1.CUST_NAME LIKE CONCAT('%',#{custName},'%' )
AND T4.PAY_TYPE=#{payType}
AND T1.CREATE_TIME >= #{createBeginTime}
AND T1.CREATE_TIME <= #{createEndTime}
AND T1.ORDER_STATUS = '1'
AND T1.ORDER_STATUS = '1'
AND (T1.ORDER_STATUS = '1' AND (T4.PAY_TYPE='1' OR T4.PAY_TYPE='3'))
AND FALSE
AND T1.ORDER_STATUS = '5'
AND T1.ORDER_STATUS = '1'
AND ((T1.ORDER_STATUS = '5' AND (T4.PAY_TYPE='1' OR T4.PAY_TYPE='3')) OR (T1.ORDER_STATUS = '1' AND T4.PAY_TYPE='2' ))
AND FALSE
AND T1.ORDER_STATUS IN ('6','12')
AND T1.ORDER_STATUS = '9'
AND T1.ORDER_STATUS = '10'
AND T1.ORDER_STATUS IN ('2','3','4','7')
AND T1.ORDER_STATUS IN ('8','11','14')
AND T2.PROVINCE = #{province}
AND T2.CITY = #{city}
AND T2.DISTRICT = #{district}
ORDER BY T1.ORDER_ID DESC

SELECT SUM(T1.ORG_TOTAL) FROM T_ORDER T1 LEFT JOIN T_SYSTEM_PAY_TYPE T4 ON T1.PAY_TYPE_ID = T4.PAY_TYPE_ID LEFT JOIN T_USERMANAGE_ENTERPRISE T2 ON T1.CUST_ID = T2.ENTERPRISE_ID WHERE T1.SUPPLY_ID = #{supplyId}
AND T1.FLOW_ID LIKE CONCAT('%',#{flowId},'%' )
AND T1.CUST_NAME LIKE CONCAT('%',#{custName},'%' )
AND T4.PAY_TYPE=#{payType}
AND T1.CREATE_TIME >= #{createBeginTime}
AND T1.CREATE_TIME <= #{createEndTime}
AND T1.ORDER_STATUS = '1'
AND T1.ORDER_STATUS = '1'
AND (T1.ORDER_STATUS = '1' AND (T4.PAY_TYPE='1' OR T4.PAY_TYPE='3'))
AND FALSE
AND T1.ORDER_STATUS = '5'
AND T1.ORDER_STATUS = '1'
AND ((T1.ORDER_STATUS = '5' AND (T4.PAY_TYPE='1' OR T4.PAY_TYPE='3')) OR (T1.ORDER_STATUS = '1' AND T4.PAY_TYPE='2' ))
AND FALSE
AND T1.ORDER_STATUS IN ('6','12')
AND T1.ORDER_STATUS = '9'
AND T1.ORDER_STATUS = '10'
AND T1.ORDER_STATUS IN ('2','3','4','7')
AND T1.ORDER_STATUS IN ('8','11','14')
AND T2.PROVINCE = #{province}
AND T2.CITY = #{city}
AND T2.DISTRICT = #{district}
AND T1.ORG_TOTAL IS NOT NULL

SELECT ,T4.PAY_TYPE,T4.PAY_TYPE_NAME FROM T_ORDER T1 LEFT JOIN T_SYSTEM_PAY_TYPE T4 ON T1.PAY_TYPE_ID = T4.PAY_TYPE_ID LEFT JOIN T_USERMANAGE_ENTERPRISE T2 ON T1.CUST_ID = T2.ENTERPRISE_ID WHERE T1.SUPPLY_ID = #{supplyId}
AND T1.FLOW_ID LIKE CONCAT('%',#{flowId},'%' )
AND T1.CUST_NAME LIKE CONCAT('%',#{custName},'%' )
AND T4.PAY_TYPE=#{payType}
AND T1.CREATE_TIME >= #{createBeginTime}
AND T1.CREATE_TIME <= #{createEndTime}
AND T1.ORDER_STATUS = '1'
AND T1.ORDER_STATUS = '1'
AND (T1.ORDER_STATUS = '1' AND (T4.PAY_TYPE='1' OR T4.PAY_TYPE='3'))
AND FALSE
AND T1.ORDER_STATUS = '5'
AND T1.ORDER_STATUS = '1'
AND ((T1.ORDER_STATUS = '5' AND (T4.PAY_TYPE='1' OR T4.PAY_TYPE='3')) OR (T1.ORDER_STATUS = '1' AND T4.PAY_TYPE='2' ))
AND FALSE
AND T1.ORDER_STATUS IN ('6','12')
AND T1.ORDER_STATUS = '9'
AND T1.ORDER_STATUS = '10'
AND T1.ORDER_STATUS IN ('2','3','4','7')
AND T1.ORDER_STATUS IN ('8','11','14')
AND T2.PROVINCE = #{province}
AND T2.CITY = #{city}
AND T2.DISTRICT = #{district}
ORDER BY T1.ORDER_ID DESC

