mapper.select:
SELECT
,T2.PAY_TYPE,T2.PAY_TYPE_NAME
FROM
T_ORDER T1
LEFT JOIN T_SYSTEM_PAY_TYPE T2 ON T1.PAY_TYPE_ID = T2.PAY_TYPE_ID
WHERE 1=1

	AND T1.FLOW_ID LIKE CONCAT('%',#{flowId},'%' )


	AND T1.SUPPLY_NAME LIKE CONCAT('%',#{supplyName},'%' )


	AND T1.CUST_NAME LIKE CONCAT('%',#{custName},'%' )


	 AND T2.PAY_TYPE=#{payType} 


	 AND T1.CREATE_TIME >= #{createBeginTime} 


	 AND T1.CREATE_TIME <= #{createEndTime} 


	AND T1.ORDER_STATUS = #{orderStatus}


	 AND T1.PAY_FLAG=#{payFlag} 

ORDER BY T1.ORDER_ID DESC
