mapper.insert:
		 INSERT INTO t_order_delivery_detail ( 


			) VALUES ( 
				 #{orderLineNo}
				, #{orderId}
				, #{flowId}
				, #{orderDetailId}
				, #{batchNumber}
				, #{deliveryProductCount}
				, #{deliveryStatus}
				, #{importFileUrl}
				, #{remark}
				, #{createUser}
				, str_to_date(#{createTime},'%Y-%m-%d %H:%i:%s')
				, #{updateUser}
				, str_to_date(#{updateTime},'%Y-%m-%d %H:%i:%s')
				, #{recieveCount}
				, #{canReturnCount}
  )

mapper.update:
 UPDATE t_order_delivery_detail SET 
	 order_delivery_detail_id=#{orderDeliveryDetailId} 
	, order_line_no=#{orderLineNo} 
	, order_id=#{orderId} 
	, flow_id=#{flowId} 
	, order_detail_id=#{orderDetailId} 
	, batch_number=#{batchNumber} 
	, delivery_product_count=#{deliveryProductCount} 
	, delivery_status=#{deliveryStatus} 
	, import_file_url=#{importFileUrl} 
	, remark=#{remark} 
	, create_user=#{createUser} 
	, create_time=str_to_date(#{createTime},'%Y-%m-%d %H:%i:%s')
	, update_user=#{updateUser} 
	, update_time=str_to_date(#{updateTime},'%Y-%m-%d %H:%i:%s')
	, recieve_count=#{recieveCount}
	, can_return_count=#{canReturnCount}
WHERE order_delivery_detail_id = #{orderDeliveryDetailId}

mapper.select:
		SELECT ODD.order_delivery_detail_id,ODD.order_line_no,ODD.batch_number,ODD.delivery_product_count,OD.order_detail_id,OD.product_code,OD.product_name,OD.brand_name,OD.form_of_drug,OD.specification,OD.manufactures,OD.product_count,ODD.recieve_count,OD.short_name from t_order_delivery_detail ODD
        LEFT JOIN t_order_detail OD ON ODD.order_detail_id=OD.order_detail_id AND ODD.order_id=OD.order_id
        LEFT JOIN t_order O ON O.flow_id=ODD.flow_id
        WHERE ODD.flow_id=#{flowId} AND ODD.delivery_status=1

	    	AND O.cust_id=#{custId}


			AND O.supply_id=#{supplyId}

SELECT ODD.order_delivery_detail_id,ODD.order_line_no,ODD.batch_number,ODD.delivery_product_count,OD.order_detail_id,OD.product_code,OD.product_name,OD.brand_name,OD.form_of_drug,OD.specification,OD.manufactures,OD.product_count,ODD.recieve_count,ODD.can_return_count from t_order_delivery_detail ODD
LEFT JOIN t_order_detail OD ON ODD.order_detail_id=OD.order_detail_id AND ODD.order_id=OD.order_id
LEFT JOIN t_order O ON O.flow_id=ODD.flow_id
WHERE ODD.flow_id=#{flowId} AND ODD.delivery_status=1

	AND O.cust_id=#{custId}


	AND O.supply_id=#{supplyId}

SELECT
FROM t_order_delivery_detail WHERE 1=1 AND order_delivery_detail_id IN
#{id}

SELECT ODD.order_delivery_detail_id,ODD.order_line_no,ODD.batch_number,ODD.delivery_product_count,OD.order_detail_id,OD.product_code,OD.product_name,OD.brand_name,OD.form_of_drug,OD.specification,OD.manufactures,OD.product_count,ODD.recieve_count,OD.short_name from t_order_delivery_detail ODD
LEFT JOIN t_order_detail OD ON ODD.order_detail_id=OD.order_detail_id
LEFT JOIN t_order_exception O ON O.exception_order_id=ODD.flow_id
WHERE ODD.delivery_status=1

	AND ODD.flow_id=#{flowId}


	AND ODD.cust_id=#{custId}


	AND O.supply_id=#{supplyId}
