mapper.update:
 UPDATE t_order_detail SET  order_detail_id=#{orderDetailId} 

	, order_id=#{orderId}


	, sku_id=#{skuId}


	, specification=#{specification}


	, supply_id=#{supplyId}


	, product_id=#{productId}


	, brand_name=#{brandName}


	, form_of_drug=#{formOfDrug}


	, product_name=#{productName}


	, product_code=#{productCode}


	, manufactures_id=#{manufacturesId}


	, manufactures=#{manufactures}


	, product_price=#{productPrice}


	, product_settlement_price=#{productSettlementPrice}


	, product_count=#{productCount}


	, recieve_count=#{recieveCount}


	, remark=#{remark}


	, create_user=#{createUser}


	, create_time=str_to_date(#{createTime},'%Y-%m-%d %H:%i:%s')


	, update_user=#{updateUser}


	, update_time=str_to_date(#{updateTime},'%Y-%m-%d %H:%i:%s')



	, short_name= #{shortName}


	, spu_code=#{spuCode}


	, cancel_produce_num=#{cancelProduceNum}

  WHERE order_detail_id = #{orderDetailId}

mapper.select:
SELECT
FROM t_order_detail WHERE 1=1 AND order_detail_id IN
#{id}
