mapper.insert:
		 INSERT INTO t_order_issued ( 


			) VALUES ( 
				 #{flowId}
				, #{supplyId}
				, #{supplyName}
				, #{issuedStatus}
				, #{issuedCount}
				, str_to_date(#{createTime},'%Y-%m-%d %H:%i:%s')
				, str_to_date(#{updateTime},'%Y-%m-%d %H:%i:%s')
				, #{cusRelationship} 
				, #{isScan} 
  )

mapper.update:
 UPDATE t_order_issued SET 
	 order_issued_id=#{orderIssuedId} 
	, flow_id=#{flowId} 
	, supply_id=#{supplyId} 
	, supply_name=#{supplyName}
	, issued_status=#{issuedStatus} 
	, issued_count=#{issuedCount} 
	, create_time=str_to_date(#{createTime},'%Y-%m-%d %H:%i:%s')
	, update_time=str_to_date(#{updateTime},'%Y-%m-%d %H:%i:%s')
	, cus_relationship=#{cusRelationship} 
	, is_scan=#{isScan} 
	, erp_order_code=#{erpOrderCode} 
WHERE order_issued_id = #{orderIssuedId}
