mapper.insert:
		 INSERT INTO t_order_settlement ( 


			) VALUES ( 
				 #{businessType}
				, #{orderId}
				, #{flowId}
				, #{custId}
				, #{custName}
				, #{supplyId}
				, #{supplyName}
				, #{confirmSettlement}
				, #{settlementMoney}
				, #{refunSettlementMoney}
				, #{payTypeId}
				, str_to_date(#{settlementTime},'%Y-%m-%d %H:%i:%s')
				, str_to_date(#{orderTime},'%Y-%m-%d %H:%i:%s')
				, #{remark}
				, #{createUser}
				, str_to_date(#{createTime},'%Y-%m-%d %H:%i:%s')
				, #{updateUser}
				, str_to_date(#{updateTime},'%Y-%m-%d %H:%i:%s')
  )

mapper.update:
 UPDATE t_order_settlement SET 
	 order_settlement_id=#{orderSettlementId} 
	, business_type=#{businessType} 
	, order_id=#{orderId} 
	, flow_id=#{flowId} 
	, cust_id=#{custId} 
	, cust_name=#{custName} 
	, supply_id=#{supplyId} 
	, supply_name=#{supplyName} 
	, confirm_settlement=#{confirmSettlement} 
	, settlement_money=#{settlementMoney}
	, refun_settlement_money = #{refunSettlementMoney}
	, pay_type_id=#{payTypeId}
	, settlement_time=str_to_date(#{settlementTime},'%Y-%m-%d %H:%i:%s')
	, order_time=str_to_date(#{orderTime},'%Y-%m-%d %H:%i:%s')
	, remark=#{remark} 
	, create_user=#{createUser} 
	, create_time=str_to_date(#{createTime},'%Y-%m-%d %H:%i:%s')
	, update_user=#{updateUser} 
	, update_time=str_to_date(#{updateTime},'%Y-%m-%d %H:%i:%s')
WHERE order_settlement_id = #{orderSettlementId}

mapper.select:

	SELECT s.order_settlement_id AS order_settlement_id, s.flow_id AS flow_id ,t.pay_name AS  pay_name,
	s.business_type AS business_type,s.cust_name AS cust_name,s.supply_name AS supply_name,
	DATE_FORMAT(s.order_time, '%Y-%m-%d %H:%i:%s') AS order_time,
	DATE_FORMAT(s.settlement_time, '%Y-%m-%d %H:%i:%s') AS settlement_time,
	s.settlement_money AS settlement_money ,s.confirm_settlement AS confirm_settlement
	FROM t_order_settlement s
	INNER JOIN t_system_pay_type t ON s.pay_type_id = t.pay_type_id
	LEFT JOIN t_passport_user u ON u.id = s.cust_id
	LEFT JOIN t_usermanage_enterprise ue ON u.enterprise_id = ue.enterprise_id

 WHERE 1 = 1 

	AND s.cust_id = #{custId}


	AND s.supply_id = #{supplyId}


	AND s.flow_id = #{flowId}


	AND s.business_type = #{businessType}


	AND s.cust_name LIKE CONCAT('%',#{custName},'%')


	AND s.supply_name LIKE CONCAT('%',#{supplyName},'%')


	AND s.confirm_settlement = #{confirmSettlement}


	AND t.pay_states = 1
	AND t.pay_type = #{payType}


	AND s.order_time > #{startTime}


	AND date_add(DATE_FORMAT(#{endTime},'%Y-%m-%d %H:%i:%s'),interval 1 day)  > s.order_time


	AND ue.province = #{province}


	AND ue.city = #{city}


	AND ue.district = #{area}
